package com.syntax.class07;

public class WhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int time=1;
		
		while (time<12) {System.out.println("Morning");
		time++;
			
		}
		
		while(time<12){
			System.out.println("Good Morning"); 
			time++;
			}
	}

}
